			},
			["preferToUpdate"] = false,
			["customText"] = "function()\n    local damage, healing = \"\", \"\"\n    if(aura_env == nil) then return \"\",\"\" end\n    if(aura_env.config.countDamage) then damage = \"D: \"..aura_env.formatLargeNumber(aura_env.damage) end\n    if(aura_env.config.countHealing) then healing = \"H: \"..aura_env.formatLargeNumber(aura_env.healing) end\n    return damage, healing\nend",
			["yOffset"] = 0,
