				["init"] = {
					["custom"] = "local e = aura_env\ne.moveOnce = false\ne.rewardProgress = {}\ne.history = {}\ne.torghastState = {}\n\nlocal torghastName =  C_QuestLog.IsQuestFlaggedCompleted(60136) and C_AreaPoiInfo.GetAreaPOIInfo(1543,6640).name or nil\n\nlocal i18n = {\n    [\"enUS\"] = {\n        [\"UNKNOWN\"] = \"UNKOWN\",\n        [\"TIMEOVER\"] = \"OverTime;\",\n        [\"STAGE\"] = \"Stage%s %s %s\",\n        [\"UNLOCKED1\"] = \"Unlocked - %s\",\n        [\"UNLOCKED2\"] = \"Unlocked - %s Level\",\n        [\"BESTMYTHICPLUS\"] = \"Best Mythic+ History\",\n        [\"MYTHIC+INFO\"] = \"+%s %s\",\n    },\n}\n\nlocal locale = GetLocale()\nlocal function L(text)\n    if i18n[locale] then return i18n[locale][text] or i18n[\"enUS\"][text] end\n    return i18n[\"enUS\"][text] or text\nend\n\nlocal moduleEnable ={\n    [\"MythicPlus\"] = e.config.moduleEnable[1],\n    [\"PvP\"] = e.config.moduleEnable[2],\n    [\"Raid\"] = e.config.moduleEnable[3],\n    [\"Torghast\"] = e.config.moduleEnable[4],\n    [\"MythicPlusList\"] = e.config.moduleEnable[5],\n} \n\nfunction e:GetModuleEnableState(typestr)\n    if type(typestr) ==\"number\" then\n        if typestr == Enum.WeeklyRewardChestThresholdType.Raid then\n            return moduleEnable[\"Raid\"]\n        elseif typestr == Enum.WeeklyRewardChestThresholdType.MythicPlus then\n            return moduleEnable[\"MythicPlus\"]\n        elseif typestr == Enum.WeeklyRewardChestThresholdType.RankedPvP then\n            return moduleEnable[\"PvP\"]\n        end  \n    end\n    return moduleEnable[typestr] or false\nend\n\n-- 对本地缓存的表进行维护\nfunction e:UpdateActivity()\n    local activityInfo =  C_WeeklyRewards.GetActivities()\n    local text\n    wipe(self.rewardProgress)\n    for _,v in pairs(activityInfo) do \n        if not self.rewardProgress[v.type] then self.rewardProgress[v.type] = {} end \n        text = self:GetRewardText(v.type,v.level) or L(\"UNKNOWN\")\n        self.rewardProgress[v.type][v.index] = {\n            id = v.id,\n            level = v.level,\n            difficulty = text,\n            progress = v.progress,\n            threshold = v.threshold,\n            unlocked = v.progress>=v.threshold,\n        }\n        if self.rewardProgress[v.type][v.index].unlocked then \n            self.rewardProgress[v.type][v.index].item = self:GetRewardItemInfo(v.id) or \"\"\n        end\n    end\nend\n\nlocal comparison = function(entry1, entry2)\n    if ( entry1.level == entry2.level ) then\n        return entry1.mapChallengeModeID < entry2.mapChallengeModeID;\n    else\n        return entry1.level > entry2.level;\n    end\nend\n-- 对本地缓存的大秘境历史记录进行更新\nfunction e:UpdateHistory()\n    local runInfo = C_MythicPlus.GetRunHistory(false,true)\n    if runInfo then\n        wipe(self.history)\n        table.sort(runInfo,comparison)\n        if #runInfo >0 then\n            local text\n            for _,v in pairs(runInfo) do \n                if v.thisWeek then\n                    text = C_ChallengeMode.GetMapUIInfo(v.mapChallengeModeID);    \n                    v.text = string.format(L(\"MYTHIC+INFO\"),v.level,text)\n                    if not v.completed and self.config.showTimeOver then\n                        v.text = v.text..\" \" .. L(\"TIMEOVER\") \n                    end\n                    table.insert(self.history,v)\n                end\n            end \n        end\n    end\n    --table.sort(self.history,comparison)\nend\n\n\nlocal function GetTorghastText(str)\n    return str:gsub(\"|n\",\"\")\nend\n\n\nfunction e:UpdateTorghast()\n    local temp = {}\n    local sets = C_UIWidgetManager.GetAllWidgetsBySetID(399)\n    wipe(self.torghastState)\n    for _,v in ipairs(sets) do \n        local widgetInfo = C_UIWidgetManager.GetTextWithStateWidgetVisualizationInfo(v.widgetID) \n        \n        if widgetInfo and widgetInfo.shownState == Enum.WidgetShownState.Shown then\n            self.torghastState[widgetInfo.orderIndex] = widgetInfo.text\n        end\n    end\n    \n    for i,v in pairs(self.torghastState) do \n        if i%2 ==0 then\n            local name = GetTorghastText(v)\n            local leveltext =GetTorghastText(self.torghastState[i+1])\n            self.torghastState[i] = string.format(\"%s - %s\",name,leveltext)\n            self.torghastState[i+1] = nil\n        end\n    end\nend\n\n-- 是否能进入 托加斯特 罪魂之塔\nfunction e.CanEnterTorghast()\n    return C_QuestLog.IsQuestFlaggedCompleted(60136) or false\nend\n\nfunction e.GetTorghastHead()\n    if not torghastName then\n        torghastName = C_AreaPoiInfo.GetAreaPOIInfo(1543,6640).name\n    end\n    return torghastName\nend\n\n\nfunction e:UpdateData()\n    self:UpdateActivity()\n    self:UpdateHistory()\n    self:UpdateTorghast()\nend\n\n-- 对特定ID的奖励物品等级进行更新\nfunction e:UpdateRewardItemLevel(id,item)\n    for _,rewards in pairs(self.rewardProgress) do \n        for _,reward in pairs(rewards) do \n            if  reward.id == id then\n                reward.item = item\n                return    \n            end\n        end\n    end\nend\n\n-- 尝试获取特定ID的奖励物品等级 如果获取不到就每秒检查一次直到获取\nfunction e:GetRewardItemInfo(id)\n    local itemLevel\n    if not id then return end\n    local itemLink = C_WeeklyRewards.GetExampleRewardItemHyperlinks(id);\n    if itemLink then\n        itemLevel =  GetDetailedItemLevelInfo(itemLink);\n        if not itemLevel then C_Timer.After(1,function() WeakAuras.ScanEvents(\"WA_UPDATE_REWARDS\",id) end) end\n        return itemLevel\n    end    \nend\n-- 获取进度种类\nfunction e:GetRewardTypeHead(type)\n    if type == Enum.WeeklyRewardChestThresholdType.Raid then\n        return  RAIDS\n    elseif type == Enum.WeeklyRewardChestThresholdType.MythicPlus then\n        return MYTHIC_DUNGEONS\n    elseif type == Enum.WeeklyRewardChestThresholdType.RankedPvP then\n        return PVP\n    end\nend\n\nfunction e:GetWeeklyProgressText(index,reward)\n    return string.format(L(\"STAGE\"),index,reward.difficulty,e:GetRewardProgressText(reward.unlocked,reward.progress,reward.threshold,reward.item))\nend\n\n-- 根据进度来更新文字和奖励物品装等显示\nfunction e:GetRewardProgressText(unlocked,progress,threshold,item)\n    if unlocked then \n        if not item then\n            return string.format(L(\"UNLOCKED1\"),RETRIEVING_ITEM_INFO) \n        else\n            return string.format(L(\"UNLOCKED2\"),item) \n        end\n    end\n    return progress..\"/\"..threshold\nend\n\n-- 获取基于何种等级的物品奖励名称\nfunction e:GetRewardText(type,level)\n    if type == Enum.WeeklyRewardChestThresholdType.Raid then\n        return  DifficultyUtil.GetDifficultyName(level);\n    elseif type == Enum.WeeklyRewardChestThresholdType.MythicPlus then\n        if level == 0 then return L(\"UNKNOWN\") end\n        return string.format(WEEKLY_REWARDS_MYTHIC, level);\n    elseif type == Enum.WeeklyRewardChestThresholdType.RankedPvP then\n        return PVPUtil.GetTierName(level);\n    end\nend\n-- 本周是否有大米历史记录（已完成\nfunction e:HasHistory()\n    return   #self.history >0\nend\n\n-- 对大米历史记录（已完成）进行字符串拼接\nfunction e:GetHistoryHead()\n    return L(\"BESTMYTHICPLUS\")..\":\"\nend\n\nfunction e:RegisterI18n(locale,tbl)\n    i18n[locale] = tbl\nend\nfunction e.sendPostEvent()\n    return WeakAuras.ScanEvents(\"WA_REWARDS_STATE\",\"POST\",e.id)\nend\n\nlocal function sendInitEvent()\n    WeakAuras.ScanEvents(\"WA_REWARDS_STATE\",\"INIT\",locale) \nend\n\n\nif not e.region.hooked then\n    local frame = PVEFrame\n    frame:HookScript(\"OnShow\",function()\n            sendInitEvent()\n            WeakAuras.ScanEvents(\"WA_UPDATE_REWARDS_OPEN\") \n            --WeakAuras.ScanEvents(\"WA_CUSTOM_COMPATIBLE\",\"WEEKLYREWARDWASELF\") \n    end)\n    frame:HookScript(\"OnHide\",function()\n            sendInitEvent()\n            WeakAuras.ScanEvents(\"WA_UPDATE_REWARDS_CLOSE\")        \n    end)\n    e.region.hooked = true\nend\n\n--提前在客户端缓存相关数据\nC_MythicPlus.RequestMapInfo()\n-- 初次更新\ne:UpdateData()\n\nWeakAuras.ScanEvents(\"WA_UPDATE_REWARDS_LIST\")\n\n\n\n\n\n",
