						["custom"] = "function(states,event,...)\n    local e = aura_env    \n    if event ==\"WA_DELAYED_PLAYER_ENTERING_WORLD\" then\n        C_Timer.After(1,e.sendPostEvent)\n    end\n    \n    for i ,v in pairs(states) do \n        v.show = false\n        v.change = true\n    end\n    if event == \"WA_UPDATE_REWARDS_CLOSE\" then return true end\n    e:UpdateData()\n    \n    \n    local num = 1\n    \n    for i,v in pairs(e.rewardProgress) do \n        num = num +1\n        states[\"HEAD\"..i] = {\n            show = e:GetModuleEnableState(i),\n            changed = true,\n            text = e:GetRewardTypeHead(i), \n            index = num,\n            isHead = true,\n        }    \n        for index,reward in pairs(v) do \n            num =  num + 1\n            states[i..index] = {\n                show = e:GetModuleEnableState(i),\n                changed = true,\n                level = reward.level,\n                unlocked = reward.unlocked,\n                value = reward.progress,\n                total = reward.threshold,\n                progress = reward.progress,\n                threshold = reward.threshold,\n                difficulty = reward.difficulty,\n                index = num,\n                isHead = false,\n            }\n            states[i..index].percent = reward.progress/reward.threshold > 1 and 100 or (reward.progress/reward.threshold) *100 \n            states[i..index].text= e:GetWeeklyProgressText(index,reward)\n        end\n        \n        local show = e:GetModuleEnableState(\"Torghast\") and e.CanEnterTorghast()\n        if show then\n            states[\"HEADTorghast\"] = {\n                show = show,\n                changed = true,\n                text = e.GetTorghastHead(),\n                index = 82,\n                isHead = true,\n            }\n            for i,v in pairs(e.torghastState) do \n                states[\"TORGHAST\"..i] = {\n                    show =show,\n                    changed=true,\n                    text = v,\n                    index = 99-i,\n                    isHead = false,\n                }\n            end\n        end\n        show = e:HasHistory() and e:GetModuleEnableState(\"MythicPlusList\")\n        if show  then\n            states[\"HEADrunHistory\"] = {\n                show = show,\n                changed= true,\n                text = e:GetHistoryHead(),\n                index = 99,\n                isHead = true,\n            }\n            \n            local maxlength = #e.history\n            \n            for i=1,e.config.mythicnum,1 do \n                if states[\"runHistory\"..i] then\n                    states[\"runHistory\"..i].show = false\n                    states[\"runHistory\"..i].changed = true\n                end\n            end\n            \n            if maxlength >= e.config.mythicnum then maxlength = e.config.mythicnum end \n            for i=1,maxlength,1 do \n                states[\"runHistory\"..i] = {\n                    show =  show,\n                    changed = true,\n                    text = e.history[i].text,\n                    completed = e.history[i].completed,\n                    index = 99+i,\n                    isHead = false,\n                }\n            end\n        end\n        \n        \n        \n    end\n    return true\nend\n\n\n",
