						["unit"] = "player",
						["custom_hide"] = "custom",
						["spellIds"] = {
						},
						["events"] = "PLAYER_REGEN_ENABLED, PLAYER_REGEN_DISABLED, UNIT_COMBAT, UPDATE_MOUSEOVER_UNIT, CLEU:UNIT_DIED",
						["subeventPrefix"] = "SPELL",
						["names"] = {
						},
						["custom_type"] = "event",
						["custom"] = "function(event, ...)\n    if (aura_env.config.requireLead and not UnitIsGroupLeader(\"player\")) then return end \n    -- doesn't do marking if not player lead and \"not lead\" is toggled in custom options\n    --handles premarked mobs\n    \n    ---------------------------------------------------------------------------------------\n    if event == \"PLAYER_REGEN_DISABLED\" or event == \"PLAYER_REGEN_ENABLED\" then\n        aura_env.marks = {}\n        aura_env.assigned = {}\n        for i = 1,40 do\n            local premarked = \"nameplate\"..i\n            if UnitExists(premarked) and GetRaidTargetIndex(premarked) and not UnitPlayerControlled(premarked) then\n                if not aura_env.mobList[UnitGUID(premarked)] then\n                    aura_env.assigned[GetRaidTargetIndex(premarked)] = true\n                    aura_env.marks[UnitGUID(premarked)] = GetRaidTargetIndex(premarked)\n                end\n            end\n            \n        end        \n        --handles premarked players\n        for unit in WA_IterateGroupMembers() do\n            if GetRaidTargetIndex(unit) then\n                aura_env.assigned[GetRaidTargetIndex(unit)] = true\n                aura_env.marks[unit] = GetRaidTargetIndex(unit)\n            end\n        end\n    end\n    \n    \n    ---------------------------------------------------------------------------------------\n    --mouseover functionality\n    if event == \"UPDATE_MOUSEOVER_UNIT\"  and aura_env.config.mouseover then\n        \n        local guid = UnitGUID(\"mouseover\")\n        if not guid then return end\n        local unitid = select(6,strsplit(\"-\",guid))\n        if UnitExists(\"mouseover\") and \n        not GetRaidTargetIndex(\"mouseover\") and \n        not aura_env.marks[\"mouseover\"] and \n        aura_env.mobList[unitid] and \n        not UnitIsDead(\"mouseover\") and \n        not aura_env.marks[UnitGUID(\"mouseover\")]then\n            \n            --handles premarked players\n            for unit in WA_IterateGroupMembers() do\n                if GetRaidTargetIndex(unit) then\n                    aura_env.assigned[GetRaidTargetIndex(unit)] = true\n                    aura_env.marks[unit] = GetRaidTargetIndex(unit)\n                end            \n            end\n            local order = aura_env.getOrder(unitid)\n            \n            for i=1, #order do\n                if not aura_env.assigned[order[i]] then\n                    aura_env.marks[guid] = order[i]\n                    aura_env.assigned[order[i]] = true\n                    SetRaidTarget(\"mouseover\", order[i])\n                    return false\n                end\n            end\n        end\n    end\n    \n    \n    \n    ---------------------------------------------------------------------------------------\n    --regular marking\n    if event == \"UNIT_COMBAT\" and aura_env.config.regularMarking then\n        local unit = select(1, ...)\n        local guid = UnitGUID(unit)\n        if not guid then return end        \n        local unitid = select(6, strsplit(\"-\", guid))\n        if UnitExists(unit) and \n        UnitAffectingCombat(unit) and \n        UnitCanAttack(\"player\", unit) and \n        not GetRaidTargetIndex(unit) and \n        not aura_env.marks[UnitGUID(unit)] and \n        aura_env.mobList[unitid] then\n            \n            \n            local order = aura_env.getOrder(unitid)\n            for i=1, #order do\n                if not aura_env.assigned[order[i]] then\n                    aura_env.marks[guid] = order[i]\n                    aura_env.assigned[order[i]] = true\n                    SetRaidTarget(unit, order[i])\n                    return\n                end                \n            end    \n        end\n        \n    end\n    \n    \n    \n    \n    ---------------------------------------------------------------------------------------\n    --Frees up marks when mobs die\n    if event == \"COMBAT_LOG_EVENT_UNFILTERED\" and select(2,...) == \"UNIT_DIED\" and aura_env.marks[select(8,...)] and UnitExists(unit) then\n        local type = select(1, strsplit(\"-\", UnitGUID(unit)))\n        if type~=\"player\" then\n            \n            aura_env.assigned[aura_env.marks[select(8,...)]] = false\n            aura_env.marks[select(8,...)] = nil\n        end\n    end    \nend",
